import React, { Component } from 'react';
import rd3 from 'react-d3-library';

import * as d3 from 'd3';

const RD3Component = rd3.Component;

class DrawChart extends Component {
    componentDidMount() {
        this.svgRoot = React.createRef();
        this.drawChart();
    }

    drawChart() {
      const data = [12, 5, 6, 6, 9, 10];
      const w = 300;
      const h = 300;
      const svg = d3.select('.single-chart')
        .append('svg')
        .attr('width', w)
        .attr('height', h);
        debugger;

        svg.selectAll('rect')
        .data(data)
        .enter()
        .append('rect')
        .attr('x', (d, i) => i * 70)
        .attr('y', (d, i) => h - 10 * d)
        .attr('width', 65)
        .attr('height', (d, i) => d * 10)
        .attr('fill', 'green');
    }


    render() {
        return (
          <div>

            <div className="single-chart" ref={this.svgRoot} style={{ height: '100%', width: '500px' }}>Chart planning here</div>
          </div>
        );
    }
}

export default DrawChart;
