import React, { Component } from 'react';
import * as d3 from 'd3';
import * as data from './tmpData';

class DrawChart extends Component {
    componentDidMount() {
        this.svgRoot = React.createRef();
        this.drawChart();
    }

    drawChart() {
      const stroke = 'rgb(36, 116, 241, 0.7)';
      const lineWithd = 1;
      const { svgData } = data;

      const w = 300;
      const h = 300;
      const line = d3.line()
        .x(d => d.x)
        .y(d => d.y);

      const svg = d3.select('.single-chart')
        .append('svg')
        .attr('width', w)
        .attr('height', h);

      svg.append('path')
        .attr('class', 'line-data')
        .attr('d', line(svgData));
      svg.append('path').attr('class', 'line-highlight');


      // svgData.map((pathData, index) => {

