      //     return pathData;
      // });
      // ;


      // svg.selectAll('rect')
      //   .data(data)
      //   .enter()
      //   .append('rect')
      //   .attr('x', (d, i) => i * 70)
      //   .attr('y', (d, i) => h - 10 * d)
      //   .attr('width', 65)
      //   .attr('height', (d, i) => d * 10)
      //   .attr('fill', 'green');
    }

    render() {
        return (
          <div>

            <div className="single-chart" ref={this.svgRoot} style={{ height: '100%', width: '500px' }}>Chart planning here</div>
          </div>
        );
    }
}

export default DrawChart;
