import React, { Component } from 'react';
import rd3 from 'react-d3-library';

const RD3Component = rd3.Component;


class DrawChart extends Component {
    componentDidMount() {
        this.svgRoot = React.createRef();
        // this.drawChart();
        // this.drawWithRd();
    }

    drawWithRd() {
      rd3;
      debugger;
      const data = [12, 5, 6, 6, 9, 10];
      const w = 300;
      const h = 300;
      const svg = rd3.select('.single-chart')
        .append('svg')
        .attr('width', w)
        .attr('height', h);

      svg.selectAll('rect')
        .data(data)
        .enter()
        .append('rect')
        .attr('x', (d, i) => i * 70)
        .attr('y', (d, i) => h - 10 * d)
        .attr('width', 65)
        .attr('height', (d, i) => d * 10)
        .attr('fill', 'green');
    }

    render() {
      const data = [12, 5, 6, 6, 9, 10];
        return (
          <div>
            <RD3Component data={data} />
            {/* <div className="single-chart" ref={this.svgRoot} style={{ height: '100%', width: '500px' }}>Chart planning here</div> */}
          </div>
        );
    }
}

export default DrawChart;
