import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import DrawChart from './DrawChart';

let selectedChart = null;

const onSelectDatarun = (datarun) => {
  console.log(selectedChart);
  debugger;
  document.querySelector('.active').classList.remove('.active');
  selectedChart.classList.add('active');
  // event.target.classList.add('active');
};

const Datarun = ({ datarun, isLoading }) => (
  <div className="time-row" onClick={() => onSelectDatarun(datarun)} ref={(currentChart) => { selectedChart = currentChart; }}>
    <ul>
      <li>{datarun.signal}</li>
      <li><DrawChart dataRun={datarun} isLoading={isLoading} /></li>
    </ul>
  </div>);

Datarun.propTypes = {
    datarun: PropTypes.object,
    isLoading: PropTypes.bool,
};

  export default Datarun;
