import { createSelector } from 'reselect';

export const getExperimentsData = (state) => state.experiments;
export const getPipelinesData = (state) => state.pipelines;
export const getDatasets = (state) => state.datasets;
export const getSelectedPipeline = (state) => state.pipelines.pipelineName;
export const getActiveProjectName = (state) => state.projects.selectedProject;

const addPipelines = (projectStack, pipelines) => projectStack.map(project => ({ ...project, pipelines }));

const isProjectsLoading = createSelector(
    [getExperimentsData, getPipelinesData, getDatasets],
    (experimentsData, pipelinesData, datasets) =>
        experimentsData.ieExperimentsLoading || datasets.isDatasetLoading || pipelinesData.isPipelinesLoading);

const groupExperimentsByProj = (experiments, criteria) => {
    const projects = [];
    const grouppedStack = experiments.reduce((result, currentValue) => {
        (result[currentValue[criteria]] = result[currentValue[criteria]] || []).push(currentValue);
        return result;
    }, []);

    Object.keys(grouppedStack).forEach(expGroup => {
        projects.push({
            experimentNum: grouppedStack[expGroup].length,
            experiments: grouppedStack[expGroup],
            name: expGroup,
            // uniquePipelineNum: countPipelines(expGroup),
            signalNum: (function() {
                switch (expGroup) {
                    case 'SMAP': return 55;
                    case 'MSL': return 27;
                    default: // For SES
                        return 71;
                }
            }()),
        });
    });

    return projects;
};


export const getProjectsList = createSelector(
    [isProjectsLoading, getExperimentsData, getDatasets, getPipelinesData, getActiveProjectName],
    (isLoadingProjects, experimentsData, dataSets, pipelinesData, activeProjectName) => {
        if (isLoadingProjects) { return []; }
        let projectsList = {};

        const grouppedExpByProject = groupExperimentsByProj(experimentsData.experimentsList, 'project');
        const pipelinedProjects = addPipelines(grouppedExpByProject, pipelinesData.pipelineList);

        const activeProject = activeProjectName || pipelinedProjects[0].name;

        const projects = pipelinedProjects.map(project => ({ ...project, isActive: project.name === activeProject }));
        projectsList = {
            isProjectsLoading: isLoadingProjects,
            projects,
        };

        return projectsList;
    },
);


export const getFilteredExperiments = createSelector(
    [getProjectsList],
    (projectsData) => {
        console.log(projectsData.projectsList && projectsData.projectsList.projects);
    },
);

