  // const width = document.querySelector('.overview-wrapper').offsetWidth - 60;
  const width = 1000;
  const height = 36;

  useEffect(() => drawChart(width, height, dataRun, onPeriodTimeChange), [dataRun, onPeriodTimeChange]);
    return (
