import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { fetchProjects } from '../../model/actions/landing';

import {
  getProjectsData,
  getPipelinesData,
  getExperimentsData,
} from '../../model/selectors/projects';


import Projects from './Projects';
import Pipelines from './Pipelines';
import Experiments from './Experiments';

import './Landing.scss';

class Landing extends Component {
  constructor(...args) {
    super(...args);
    this.filterExperimentsBy = this.filterExperimentsBy.bind(this);
  }

  componentDidMount() {
    this.props.fetchProjectsList();
  }

  filterExperimentsBy(pipeline) {
    const { name } = pipeline;
    const { experimentsList } = this.props.experimentsData;
    const filtered = experimentsList.forEach(experiment => experiment.pipeline === name);
    console.log(filtered);
    debugger;

    let heroes = [
      { name: 'Batman', franchise: 'DC' },
      { name: 'Ironman', franchise: 'Marvel' },
      { name: 'Thor', franchise: 'Marvel' },
      { name: 'Superman', franchise: 'DC' },
    ];

    let marvelHeroes = heroes.filter((hero) => {
      return hero.franchise == "Marvel";
    });
    // experimentsList.filter((experiment) => experiment.pipeline = pipeline.name);
  }

  render() {
    const {
      projectsData,
      pipelinesData,
      experimentsData,
    } = this.props;

    return (
      <div>
        <Projects projects={projectsData} />
        <Pipelines pipeLines={pipelinesData} onFilterExperiments={this.filterExperimentsBy} />
        <Experiments experiments={experimentsData} />
      </div>
    );
  }
}

Landing.propTypes = {
    fetchProjectsList: PropTypes.func,
    projectsData: PropTypes.object,
    pipelinesData: PropTypes.object,
    experimentsData: PropTypes.object,

};

export const mapStateToProps = state => ({
  projectsData: getProjectsData(state),
  pipelinesData: getPipelinesData(state),
  experimentsData: getExperimentsData(state),
});

export const mapDispatchToProps = dispatch => ({
  fetchProjectsList: () => dispatch(fetchProjects()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Landing);
