import rd3 from 'react-d3-library';

import d3, { select } from 'd3';

const RD3Component = rd3.Component;

class DrawChart extends Component {
    componentDidMount() {
        console.log('mounted');
        this.svgRoot = React.createRef();
    }


    render() {
      console.log(RD3Component);
      const node = d3.select();

        return (
          <div>
            <RD3Component />
            <div ref={this.svgRoot} style={{ height: '100%' }} />
          </div>
        );
    }
}

export default DrawChart;
