import React, { Component } from 'react';
import PropTypes from 'prop-types';
import * as d3 from 'd3';

class DrawChart extends Component {
  constructor(props) {
    super(props);
    // this.width = document.querySelector('.overview-wrapper').offsetWidth;
    this.width = 1000;
    this.height = 36;
  }

    componentDidMount() {
        this.drawChart();
    }

    UNSAFE_componentWillReceiveProps(nextProps) {
      if (nextProps.selectedPeriod !== this.props.selectedPeriod) {
        console.log(nextProps.selectedPeriod);
      }
    }

    getScale(w, h) {
      let minValue = Number.MAX_SAFE_INTEGER;
      let maxValue = Number.MIN_SAFE_INTEGER;
      const { timeSeries } = this.props.dataRun;
      const timeSeriesMin = timeSeries[0][0];
      const timeSeriesMax = timeSeries[timeSeries.length - 1][0];
      const x = d3.scaleTime().range([0, w]);
      const y = d3.scaleLinear().range([h, 0]);

      minValue = minValue > timeSeriesMin ? timeSeriesMin : minValue;
      maxValue = maxValue < timeSeriesMax ? timeSeriesMax : maxValue;
      x.domain([minValue, maxValue]);
      y.domain([-1, 1]);

      return { x, y };
    }

    drawChart() {
      const width = this.width - 10;

      const { timeSeries, eventWindows } = this.props.dataRun;
      const { x, y } = this.getScale(width, this.height);
      const line = d3.line()
        .x(d => x(d[0]))
        .y(d => y(d[1]));

      const highlightedEvents = eventWindows.map(event => timeSeries.slice(event[0], event[1] + 2));
      const svg = d3.select(`._${this.props.dataRun.id}`)
        .append('svg')
        .attr('width', width)
        .attr('class', 'wave-chart');

      svg.append('path')
        .attr('class', 'wave-data')
        .attr('d', line(timeSeries))
        .attr('transform', 'translate(10, 6)');

      highlightedEvents.map(event =>
        svg.append('path')
          .attr('class', 'wave-event')
          .attr('transform', 'translate(10, 6)')
          .attr('d', line(event)),
      );
      this.drawBrush(svg);
    }

    drawBrush (element) {
      const { width } = this;
      const height = 43;
      const xRange = d3.scaleTime().range([0, width]);
      const brush = d3.brushX()
        .extent([[0, 0], [width, height]]);
      const brushContext = element.append('g').attr('class', 'brushContext');

      brushContext.append('g')
        .attr('class', 'brush')
        .attr('transform', 'translate(5, 3)')
        .call(brush)
        .call(brush.move, xRange.range());

        brush.on('brush end', brushChange.bind(this));

      function brushChange() {
        if (d3.event.sourceEvent && d3.event.sourceEvent.type === 'zoom') return;
        this.props.onPeriodTimeChange(d3.event.selection);
      }
    }

    render() {
      return (
        <div className={`_${this.props.dataRun.id}`} /> // @TODO - find a better way to target this element
      );
    }
}

DrawChart.propTypes = {
  dataRun: PropTypes.object,
  onPeriodTimeChange: PropTypes.func,
};

export default DrawChart;
