import React from 'react';
import PropTypes from 'prop-types';

import { connect } from 'react-redux';
import DrawChart from './DrawChart';

import { onSelectDatarun } from '../../../model/actions/datarun';
import { getSelectedDatarunID } from '../../../model/selectors/datarun';

const Datarun = ({ datarun, isLoading, selectDatarun, selectedDatarunID }) => {
    const activeClass = datarun.id === selectedDatarunID ? 'active' : '';
    return (
      <div className={`time-row ${activeClass}`} onClick={() => selectDatarun(datarun.id)}>
        <ul>
          <li>{datarun.signal}</li>
          <li><DrawChart dataRun={datarun} isLoading={isLoading} /></li>
        </ul>
      </div>
  );
};

Datarun.propTypes = {
    datarun: PropTypes.object,
    isLoading: PropTypes.bool,
    selectDatarun: PropTypes.func,
    selectedDatarunID: PropTypes.string,
};

export default connect(state => ({
    selectedDatarunID: getSelectedDatarunID(state),
}), dispatch => ({
    selectDatarun: (datarunID) => dispatch(onSelectDatarun(datarunID)),
}))(Datarun);

// export default Datarun;
