import React, { Component } from 'react';
import * as d3 from 'd3';
import * as data from './tmpData';

const { svgData } = data;

class DrawChart extends Component {
    componentDidMount() {
        this.svgRoot = React.createRef();
        this.drawChart();
    }

    getScale(w, h) {
      const x = d3.scaleTime().range([0, w]);
      x.domain([1222819200000, 1468864800000]);

      const y = d3.scaleLinear().range([h, 0]);
      y.domain([-1, 1]);
      return { x, y };
    }

    drawChart() {
      const { timeseries } = this.props.dataRun;
      const stroke = 'rgb(36, 116, 241, 0.7)';
      const strokeWidth = 1;

      const w = 300;
      const h = 20;
      const { x, y } = this.getScale(900, 20);
      const line = d3.line()
        .x(d => x(d[0]))
        .y(d => y(d[1]));

      const svg = d3.select('.single-chart')
        .append('svg')
        .attr('width', w)
        .attr('height', h);

      svg.append('path')
        .attr('stroke', stroke)
        .attr('stroke-width', strokeWidth)
        .attr('class', 'line-data')
        .attr('d', line(timeseries));
    }

    render() {
      return (
        <div>
          <div className="single-ctx-chart" />
        </div>
      );
    }
}

export default DrawChart;
